function renderTechnologies() {
    document.querySelectorAll(".tool-tags").forEach((element) => {
        let technology = "";
        if (element.classList.contains("tool-tags-checkboxes")) {
            // array with all tool ids from technologies array
            technology = technologies.map(tech => tech.id);
        } else {
            const projectTechnologies = element.dataset.technologies;
            technology = projectTechnologies.split(",").map(tech => tech.trim());
        }


        let html = [];
        for (let i = 0; i < technology.length; i++) {
            const tech = technologies.find(t => t.id === technology[i].toLowerCase());
            if (tech) {
                if (element.classList.contains("tool-tags-checkboxes")) {
                    // If element has class of tool-tags-checkboxes, show a checkbox
                    // If the technology html is not already in html, add it
                    html.push(`<label class="tool-tag-checkbox"><input type="checkbox" name="tool" value="${tech.id}" class="tool-tag-checkbox-input-${tech.id}"><div class="tool-tag ${tech.class}">${tech.svg}<span>${tech.name}</span></div></label>`);
                } else {
                    // Show the language, technology, or tool if it exists
                    html.push(`<a class="tool-tag-link" href="/search?tool=${tech.id}"><div class="tool-tag ${tech.class}">${tech.svg}<span>${tech.name}</span></div></a>`);
                }
            } else {
                // If element doesn't have class of education-relevant-coursework-tools, show a message
                if (!element.classList.contains("education-relevant-coursework-tools")) {
                    html.push(`<p style="color: var(--form-input-placeholder-color); font-size: 1rem; font-weight: 200;">No associated technology or tool</p>`);
                }
            }
        }
        element.innerHTML = html.join("");
    });
}
renderTechnologies(); // Run on initial load